        // スライド切り替え機能
        let currentSlide = 1;
        const totalSlides = 20;

        document.getElementById('totalSlides').textContent = totalSlides;

        function showSlide(slideNumber) {
            // 全てのスライドを非表示
            document.querySelectorAll('.slide').forEach(slide => {
                slide.classList.remove('active-slide');
            });

            // 指定されたスライドを表示
            document.getElementById('slide' + slideNumber).classList.add('active-slide');

            // カウンターを更新
            document.getElementById('currentSlide').textContent = slideNumber;

            // 前へボタンを有効/無効に
            document.getElementById('prevButton').style.opacity = slideNumber === 1 ? '0.5' : '1';
            document.getElementById('prevButton').style.pointerEvents = slideNumber === 1 ? 'none' : 'auto';

            // 次へボタンを有効/無効に
            document.getElementById('nextButton').style.opacity = slideNumber === totalSlides ? '0.5' : '1';
            document.getElementById('nextButton').style.pointerEvents = slideNumber === totalSlides ? 'none' : 'auto';

            // スクロールを上部に戻す
            window.scrollTo(0, 0);
        }

        function nextSlide() {
            if (currentSlide  1) {
                currentSlide--;
                showSlide(currentSlide);
            }
        }

        // ボタンイベントリスナー
        document.getElementById('nextButton').addEventListener('click', nextSlide);
        document.getElementById('prevButton').addEventListener('click', prevSlide);

        // キーボードナビゲーション
        document.addEventListener('keydown', function(event) {
            if (event.key === 'ArrowRight') {
                nextSlide();
            } else if (event.key === 'ArrowLeft') {
                prevSlide();
            }
        });

        // 診断ツール機能
        function updateDiagnosisResults() {
            const score1 = parseInt(document.getElementById('slider1').value);
            const score2 = parseInt(document.getElementById('slider2').value);
            const score3 = parseInt(document.getElementById('slider3').value);
            const score4 = parseInt(document.getElementById('slider4').value);
            const score5 = parseInt(document.getElementById('slider5').value);

            const totalScore = score1 + score2 + score3 + score4 + score5;
            document.getElementById('totalScore').textContent = totalScore;

            let resultType, resultDetails;

            if (totalScore >= 5 && totalScore = 11 && totalScore
